mapper.select:
    SELECT
		*
	FROM
		t_cj_xld 


			COLLECT_ID=#{collectId}


			AND AMNAME LIKE concat('%',#{amname},'%')


			AND DXLX=#{dxlx}


			AND TYPE=#{type}


			AND XL_COLLECT_ID=#{xlCollectId}


			AND XL_COLLECT_ID in (select COLLECT_ID from t_cj_line where AMNAME LIKE concat('%',#{xlAmname},'%'))


	ORDER BY XL_COLLECT_ID,AMNAME

	SELECT
		*
	FROM
		t_cj_xld 


			COLLECT_ID=#{collectId}


			AND AMNAME LIKE concat('%',#{amname},'%')


			AND DXLX=#{dxlx}


			AND TYPE=#{type}


			AND XL_COLLECT_ID=#{xlCollectId}


			AND XL_COLLECT_ID in (select COLLECT_ID from t_cj_line where AMNAME LIKE concat('%',#{xlAmname},'%'))


	ORDER BY XL_COLLECT_ID,AMNAME
  	limit #{firstResult},#{maxResult}

  	select
		count(*)
	FROM
		t_cj_xld 


			COLLECT_ID=#{collectId}


			AND AMNAME LIKE concat('%',#{amname},'%')


			AND DXLX=#{dxlx}


			AND TYPE=#{type}


			AND XL_COLLECT_ID=#{xlCollectId}


			AND XL_COLLECT_ID in (select COLLECT_ID from t_cj_line where AMNAME LIKE concat('%',#{xlAmname},'%'))
