mapper.select:
SELECT count(1) FROM pro_camera b LEFT JOIN pro_spot c ON b.SPOT_CODE = c.SPOT_CODE WHERE b.SYS_PROCESS_FLAG = '1'
and DEVICE_NAME like concat('%',#{deviceName},'%')
and status like #{status}

SELECT b.rowid, b.DEVICE_NO, b.DEVICE_NAME, b.DEVICE_CHANNEL, b.STATUS, b.SPOT_CODE, b.ASSET_CODE, b.BUY_DATE, c.SPOT_NAME FROM pro_camera b LEFT JOIN pro_spot c ON b.SPOT_CODE = c.SPOT_CODE WHERE b.SYS_PROCESS_FLAG = '1'
and DEVICE_NAME like concat('%',#{deviceName},'%')
and status like #{status}
limit #{firstResult},#{maxResult}

mapper.insert:
insert into pro_camera (rowid, DEVICE_NO, DEVICE_NAME, 
  DEVICE_CHANNEL, STATUS, SPOT_CODE, 
  ASSET_CODE, BUY_DATE, SYS_CREATE_TIME, 
  SYS_UPDATE_TIME, SYS_DELETE_TIME, SYS_PROCESS_FLAG
  )
values (#{rowid,jdbcType=BIGINT}, #{deviceNo,jdbcType=VARCHAR}, #{deviceName,jdbcType=VARCHAR}, 
  #{deviceChannel,jdbcType=VARCHAR}, #{status,jdbcType=CHAR}, #{spotCode,jdbcType=VARCHAR}, 
  #{assetCode,jdbcType=VARCHAR}, #{buyDate,jdbcType=VARCHAR}, #{sysCreateTime,jdbcType=TIMESTAMP}, 
  #{sysUpdateTime,jdbcType=TIMESTAMP}, #{sysDeleteTime,jdbcType=TIMESTAMP}, #{sysProcessFlag,jdbcType=CHAR}
  )
