mapper.select:
select count(*) from pro_card where SYS_PROCESS_FLAG='1'
and STATUS=#{status}
and CARD_NO like concat('%',#{cardNo},'%')

SELECT rowid, COMPANY_CODE, CARD_NO, FACTORY, MODEL, STATUS, REMARK, ASSET_CODE, BUY_DATE from pro_card where SYS_PROCESS_FLAG='1'
and STATUS=#{status}
and CARD_NO like concat('%',#{cardNo},'%')
limit #{firstResult},#{maxResult}
