mapper.select:
	select 
    	t1.ROWID, t1.MONITOR_POINT_CODE, t1.MONITOR_VALUE, t1.SUBMIT_TIME
    from pm_scene_monitor_point_his t1
    where 1=1

    and t1.MONITOR_POINT_CODE = #{monitorPointCode,jdbcType=VARCHAR}


    and t1.SUBMIT_TIME >= #{startTime, jdbcType=TIMESTAMP}


    and t1.SUBMIT_TIME <= #{endTime, jdbcType=TIMESTAMP}
