mapper.insert:
insert into pm_quality_problem ( COMPANY_CODE, DEVICE_TYPE_CODE, 
  PROBLEM_NAME, REMARKS
  )
values (#{companyCode,jdbcType=VARCHAR}, #{deviceTypeCode,jdbcType=VARCHAR}, 
  #{problemName,jdbcType=VARCHAR}, #{remarks,jdbcType=VARCHAR}
  )

mapper.select:
select t1.*, t2.device_type_name from pm_quality_problem t1 left join power_device_type t2 on t2.device_type_code = t1.device_type_code where 1=1
and t1.device_type_code = #{deviceTypeCode}
limit #{firstResult},#{maxResult}
