mapper.select:
select 
	count(*)
from power_device_base_attr_field t1
left join pm_base t2 on t2.pro_no=t1.pro_no
left join power_device_type t3 on t3.device_type_code = t1.device_type_code
where 1=1
and t1.company_code = #{companyCode}

and t1.pro_no = #{proNo}


and t1.device_type_code = #{deviceTypeCode}

select
	t1.*, t2.pro_name, t3.device_type_name
from power_device_base_attr_field t1
left join pm_base t2 on t2.pro_no=t1.pro_no
left join power_device_type t3 on t3.device_type_code = t1.device_type_code
where 1=1
and t1.company_code = #{companyCode}

and t1.pro_no = #{proNo}


and t1.device_type_code = #{deviceTypeCode}

limit #{firstResult},#{maxResult}
