mapper.select:
SELECT line.CITY, line.BDZ_COLLECT_ID, bdz.AMNAME AS BDZAMNAME, line.COLLECT_ID AS XL_COLLECT_ID, line.AMNAME AS XLAMNAME, byq.COLLECT_ID, byq.AMNAME, byq.RL, byq.TYPE, byq.JD, byq.WD FROM t_cj_byq byq INNER JOIN (SELECT XL_COLLECT_ID AS XLINTERID, FROM_ID AS id, FROM_TYPE AS TYPE FROM t_cj_xld WHERE (FROM_TYPE = 9) UNION SELECT XL_COLLECT_ID AS XLINTERID, TO_ID AS id, TO_TYPE AS TYPE FROM t_cj_xld WHERE (TO_TYPE = 9)) xld ON byq.COLLECT_ID = xld.id INNER JOIN t_cj_line line ON xld.XLINTERID = line.COLLECT_ID INNER JOIN t_cj_bdz bdz ON line.BDZ_COLLECT_ID = bdz.COLLECT_ID
line.CITY LIKE concat('%',#{city},'%')
AND bdz.AMNAME LIKE concat('%',#{amname},'%')
AND RL=#{rl}
AND TYPE=#{type}
AND BDZ_COLLECT_ID=#{bdzCollectId}
AND bdz.AMNAME LIKE concat('%',#{bdzAmname},'%')
AND line.COLLECT_ID=#{xlCollectId}
AND line.AMNAME LIKE concat('%',#{xlAmname},'%')
ORDER BY BDZAMNAME,XLAMNAME,byq.AMNAME

SELECT line.CITY, line.BDZ_COLLECT_ID, bdz.AMNAME AS BDZAMNAME, line.COLLECT_ID AS XL_COLLECT_ID, line.AMNAME AS XLAMNAME, byq.COLLECT_ID, byq.AMNAME, byq.RL, byq.TYPE, byq.JD, byq.WD FROM t_cj_byq byq INNER JOIN (SELECT XL_COLLECT_ID AS XLINTERID, FROM_ID AS id, FROM_TYPE AS TYPE FROM t_cj_xld WHERE (FROM_TYPE = 9) UNION SELECT XL_COLLECT_ID AS XLINTERID, TO_ID AS id, TO_TYPE AS TYPE FROM t_cj_xld WHERE (TO_TYPE = 9)) xld ON byq.COLLECT_ID = xld.id INNER JOIN t_cj_line line ON xld.XLINTERID = line.COLLECT_ID INNER JOIN t_cj_bdz bdz ON line.BDZ_COLLECT_ID = bdz.COLLECT_ID
line.CITY LIKE concat('%',#{city},'%')
AND bdz.AMNAME LIKE concat('%',#{amname},'%')
AND RL=#{rl}
AND TYPE=#{type}
AND BDZ_COLLECT_ID=#{bdzCollectId}
AND bdz.AMNAME LIKE concat('%',#{bdzAmname},'%')
AND line.COLLECT_ID=#{xlCollectId}
AND line.AMNAME LIKE concat('%',#{xlAmname},'%')
ORDER BY BDZAMNAME,XLAMNAME,byq.AMNAME limit #{firstResult},#{maxResult}

SELECT count(*) FROM t_cj_byq byq INNER JOIN (SELECT XL_COLLECT_ID AS XLINTERID, FROM_ID AS id, FROM_TYPE AS TYPE FROM t_cj_xld WHERE (FROM_TYPE = 9) UNION SELECT XL_COLLECT_ID AS XLINTERID, TO_ID AS id, TO_TYPE AS TYPE FROM t_cj_xld WHERE (TO_TYPE = 9)) xld ON byq.COLLECT_ID = xld.id INNER JOIN t_cj_line line ON xld.XLINTERID = line.COLLECT_ID INNER JOIN t_cj_bdz bdz ON line.BDZ_COLLECT_ID = bdz.COLLECT_ID
line.CITY LIKE concat('%',#{city},'%')
AND bdz.AMNAME LIKE concat('%',#{amname},'%')
AND RL=#{rl}
AND TYPE=#{type}
AND BDZ_COLLECT_ID=#{bdzCollectId}
