mapper.select:
SELECT line.CITY, line.BDZ_COLLECT_ID, bdz.AMNAME AS BDZAMNAME, line.COLLECT_ID AS XL_COLLECT_ID, line.AMNAME AS XLAMNAME, fzx.COLLECT_ID, fzx.AMNAME, fzx.ZBRL, fzx.ZBNUM, fzx.MODEL, fzx.XB_TYPE, fzx.JD, fzx.WD FROM t_cj_fzx fzx INNER JOIN (SELECT XL_COLLECT_ID AS XLINTERID, FROM_ID AS id, FROM_TYPE AS TYPE FROM t_cj_xld WHERE (FROM_TYPE = 18) UNION SELECT XL_COLLECT_ID AS XLINTERID, TO_ID AS id, TO_TYPE AS TYPE FROM t_cj_xld WHERE (TO_TYPE = 18)) xld ON fzx.COLLECT_ID = xld.id INNER JOIN t_cj_line line ON xld.XLINTERID = line.COLLECT_ID INNER JOIN t_cj_bdz bdz ON line.BDZ_COLLECT_ID = bdz.COLLECT_ID
line.CITY LIKE concat('%',#{city},'%')
AND fzx.AMNAME LIKE concat('%',#{amname},'%')
AND MODEL=#{model}
AND XB_TYPE=#{xbType}
AND BDZ_COLLECT_ID=#{bdzCollectId}
AND bdz.AMNAME LIKE concat('%',#{bdzAmname},'%')
AND line.COLLECT_ID=#{xlCollectId}
AND line.AMNAME LIKE concat('%',#{xlAmname},'%')
ORDER BY line.CITY,BDZAMNAME,XLAMNAME,fzx.AMNAME

SELECT line.CITY, line.BDZ_COLLECT_ID, bdz.AMNAME AS BDZAMNAME, line.COLLECT_ID AS XL_COLLECT_ID, line.AMNAME AS XLAMNAME, fzx.COLLECT_ID, fzx.AMNAME, fzx.ZBRL, fzx.ZBNUM, fzx.MODEL, fzx.XB_TYPE, fzx.JD, fzx.WD FROM t_cj_fzx fzx INNER JOIN (SELECT XL_COLLECT_ID AS XLINTERID, FROM_ID AS id, FROM_TYPE AS TYPE FROM t_cj_xld WHERE (FROM_TYPE = 18) UNION SELECT XL_COLLECT_ID AS XLINTERID, TO_ID AS id, TO_TYPE AS TYPE FROM t_cj_xld WHERE (TO_TYPE = 18)) xld ON fzx.COLLECT_ID = xld.id INNER JOIN t_cj_line line ON xld.XLINTERID = line.COLLECT_ID INNER JOIN t_cj_bdz bdz ON line.BDZ_COLLECT_ID = bdz.COLLECT_ID
line.CITY LIKE concat('%',#{city},'%')
AND fzx.AMNAME LIKE concat('%',#{amname},'%')
AND MODEL=#{model}
AND XB_TYPE=#{xbType}
AND BDZ_COLLECT_ID=#{bdzCollectId}
AND bdz.AMNAME LIKE concat('%',#{bdzAmname},'%')
AND line.COLLECT_ID=#{xlCollectId}
AND line.AMNAME LIKE concat('%',#{xlAmname},'%')
ORDER BY line.CITY,BDZAMNAME,XLAMNAME,fzx.AMNAME limit #{firstResult},#{maxResult}

SELECT count(*) FROM t_cj_fzx fzx INNER JOIN (SELECT XL_COLLECT_ID AS XLINTERID, FROM_ID AS id, FROM_TYPE AS TYPE FROM t_cj_xld WHERE (FROM_TYPE = 18) UNION SELECT XL_COLLECT_ID AS XLINTERID, TO_ID AS id, TO_TYPE AS TYPE FROM t_cj_xld WHERE (TO_TYPE = 18)) xld ON fzx.COLLECT_ID = xld.id INNER JOIN t_cj_line line ON xld.XLINTERID = line.COLLECT_ID INNER JOIN t_cj_bdz bdz ON line.BDZ_COLLECT_ID = bdz.COLLECT_ID
line.CITY LIKE concat('%',#{city},'%')
AND fzx.AMNAME LIKE concat('%',#{amname},'%')
AND MODEL=#{model}
AND XB_TYPE=#{xbType}
AND BDZ_COLLECT_ID=#{bdzCollectId}
