mapper.select:
  	select count(*)
  	from cp_commute_config t1
  	left join pm_base t2 on t1.pro_no=t2.pro_no

  	and t2.MANAGE_DEPT=#{deptCode}

  	where 1=1

  	and t1.pro_no=#{proNo, jdbcType=VARCHAR}


  	and t1.company_code=#{companyCode, jdbcType=VARCHAR}


	and t2.DISTRICT_MANAGER=#{districtManager}


	and t2.EMPLOYEECODE=#{proManager}

  	select 
  	t1.*,t2.pro_name
  	from cp_commute_config t1
  	left join pm_base t2 on t1.pro_no=t2.pro_no

  	and t2.MANAGE_DEPT=#{deptCode}

  	where 1=1

  	and t1.pro_no=#{proNo, jdbcType=VARCHAR}


  	and t1.company_code=#{companyCode, jdbcType=VARCHAR}




	and t2.DISTRICT_MANAGER=#{districtManager}


	and t2.EMPLOYEECODE=#{proManager}


  	limit #{firstResult},#{maxResult}
