mapper.select:
select count(*) from fm_fee_type 
where SYS_PROCESS_FLAG='1' and   company_code=#{companyCode} 

	and FEE_PARENT_CODE=#{feeParentCode}

  	select  rowid, FEE_CODE, FEE_NAME, FEE_PARENT_CODE, LV, SYS_CREATE_TIME, SYS_UPDATE_TIME, 
    SYS_DELETE_TIME, SYS_PROCESS_FLAG, REMARKS,COMPANY_CODE
  	from fm_fee_type  a
  	where a.SYS_PROCESS_FLAG='1' and a.company_code=#{companyCode} 

  		and a.FEE_PARENT_CODE=#{feeParentCode}

	order by a.rowid
  	limit #{firstResult},#{maxResult}
