mapper.select:
select count(*) from gm_asset_class
 where sys_process_flag='1'

and ASSET_CHARACTER_CODE=#{assetCharacterCode}

	select 

   from gm_asset_class a
   join FM_ASSET_CHARACTER b on a.ASSET_CHARACTER_CODE=b.ASSET_CHARACTER_CODE
   where a.sys_process_flag='1'

	and a.ASSET_CHARACTER_CODE=#{assetCharacterCode}

	limit #{firstResult},#{maxResult}

mapper.insert:

insert into gm_asset_class (ASSET_CLASS_CODE, ASSET_CLASS_NAME, 
  ASSET_CHARACTER_CODE, RESIDUAL_RATE, USEFUL_LIFE, 
  EUP_MONTH, SYS_CREATE_TIME, SYS_PROCESS_FLAG, REMARKS,IS_CM
  )
values (#{assetClassCode,jdbcType=VARCHAR}, #{assetClassName,jdbcType=VARCHAR}, 
  #{assetCharacterCode,jdbcType=VARCHAR}, #{residualRate,jdbcType=REAL}, #{usefulLife,jdbcType=INTEGER}, 
  #{eupMonth,jdbcType=INTEGER}, now(), '1', #{remarks,jdbcType=VARCHAR},#{iscm}
  )
