mapper.select:
 select 

    from goods b
  WHERE
	b.SYS_PROCESS_FLAG = '1'  	
	AND b.GOODS_CODE=#{goodsCode}
	AND b.SPEC_CODE =#{specCode}
	AND b.STORE_CODE =#{storeCode}

SELECT b.GOODS_NAME goodsName, b.SPEC_NAME specName, b.STOCK stock, c.STORE_NAME stockName FROM goods b LEFT JOIN storehouse c ON b.STORE_CODE = c.STORE_CODE WHERE b.SYS_PROCESS_FLAG = '1'
and b.GOODS_NAME like concat('%',#{goodsName},'%')
limit #{firstResult},#{maxResult}
