mapper.select:
select count(*) from gm_vm_fuelcard_recharge
where SYS_PROCESS_FLAG='1' and FUELCARD_NO=#{fuelcardNo}

and RECHARGE_DATE >=#{fromDate}


and RECHARGE_DATE <=#{toDate}

	select 

  from gm_vm_fuelcard_recharge
  where SYS_PROCESS_FLAG='1' and FUELCARD_NO=#{fuelcardNo}

	and RECHARGE_DATE >=#{fromDate}


	and RECHARGE_DATE <=#{toDate}

	limit #{firstResult},#{maxResult}
