mapper.select:
  	select count(*)
  	from gm_bm_bedrent t1
  	join (select PRO_NO,PRO_NAME from PM_BASE 
			where COMPANY_CODE=#{companyCode} and SYS_PROCESS_FLAG='1' and PRO_STATUS=2

			and ACCOUNT_CODE=#{accountCode}


			and DISTRICT_MANAGER=#{districtManager}


				and MANAGE_DEPT=#{deptCode}

	) t2 on t2.PRO_NO = t1.PRO_NO
	left join gm_bm_dorm t3 on t3.DORM_CODE = t1.DORM_CODE
  	where 1=1 and t1.RENT_STATUS <> 2

  	and t1.PRO_NO = #{proNo}


  	and t1.EMPLOYEENAME like "%"#{employeename}"%"

  	select t1.*,t2.PRO_NAME, t3.DORM_NAME
  	from gm_bm_bedrent t1
  	join (select PRO_NO,PRO_NAME from PM_BASE 
			where COMPANY_CODE=#{companyCode} and SYS_PROCESS_FLAG='1' and PRO_STATUS=2

			and ACCOUNT_CODE=#{accountCode}


			and DISTRICT_MANAGER=#{districtManager}


				and MANAGE_DEPT=#{deptCode}

	) t2 on t2.PRO_NO = t1.PRO_NO
	left join gm_bm_dorm t3 on t3.DORM_CODE = t1.DORM_CODE
  	where 1=1 and t1.RENT_STATUS <> 2

  	and t1.PRO_NO = #{proNo}


  	and t1.EMPLOYEENAME like "%"#{employeename}"%"

  	order by t1.RENT_STATUS, t1.RENT_DATE DESC
  	limit #{firstResult},#{maxResult}
