mapper.select:
select
	t1.*, t2.pro_name
from pm_device_health t1
left join pm_base t2 on t2.pro_no=t1.pro_no
where t1.company_code = #{companyCode}

and t1.pro_no = #{proNo}

select
	t1.*, t2.pro_name
from pm_device_health t1
left join pm_base t2 on t2.pro_no=t1.pro_no
where 1=1
and t1.company_code = #{companyCode}

and t1.pro_no = #{proNo}

limit #{firstResult},#{maxResult}
