mapper.select:
	select count(*) from qa_questionnire
	where 1=1

  	and (title like "%"#{searchName}"%" or
  		descr like "%"#{searchName}"%")


  	and status = #{status, jdbcType=INTEGER}


  	and type = #{type, jdbcType=INTEGER}

SELECT t1.ROWID, t1.QUESTIONNIRE_CODE, t1.TITLE, t1.DESCR, DATE_FORMAT(t1.ENABLED_BEGIN_TIME, '%Y-%m-%d %H:%i:%s') as ENABLED_BEGIN_TIME, DATE_FORMAT(t1.ENABLED_END_TIME, '%Y-%m-%d %H:%i:%s') as ENABLED_END_TIME, t1.STATUS, t1.SYS_CREATE_TIME, t1.CREATER, t3.EMPLOYEENAME, t1.TYPE, t1.sendedCnt, COUNT(t2.submit_user) AS answerSheetCnt FROM (SELECT tt1.*, COUNT(tt2.employee_code) AS sendedCnt FROM qa_questionnire tt1 LEFT JOIN qa_questionnire_send tt2 ON tt1.questionnire_code = tt2.questionnire_code GROUP BY tt1.questionnire_code ) t1 LEFT JOIN qa_answer_sheet t2 ON t1.questionnire_code = t2.questionnire_code LEFT JOIN md_employee t3 ON t1.creater = t3.employeecode where 1=1
and (t1.title like "%"#{searchName}"%" or t1.descr like "%"#{searchName}"%")
and t1.status = #{status, jdbcType=INTEGER}
and t1.type = #{type, jdbcType=INTEGER}
GROUP BY t1.questionnire_code limit #{firstResult},#{maxResult}
